mapper.insert:
INSERT INTO notice (
	   userId
	 , userName
	 , title
	 , content)
VALUES (
	   #{userId}
	 , #{userName}
	 , #{title}
	 , #{content})

mapper.update:
UPDATE notice
   SET title		= #{title}
     , content		= #{content}
     , updateUser	= #{userId}
     , updateTime	= current_timestamp
 WHERE boardNum 	= #{boardNum}

mapper.insert:
INSERT INTO qna (
	   groupNo
	 , groupStep
	 , userId
	 , userName
	 , title
	 , content
	 , password
	 , chkSecret )
VALUES (
	   #{groupNo}
	 , (SELECT groupStep
	 	  FROM qna as A
	 	 WHERE boardNum = #{groupNo}) +1
	 , #{userId}
	 , #{userName}
	 , #{title}
	 , #{content}
	 , #{password}
	 , "reply")

