mapper.select:
SELECT productNo,fileName
  FROM productImg
 WHERE productNo = #{productNo}

SELECT productNo,colorName
  FROM productColor
 WHERE productNo = #{productNo}

SELECT productNo,sizeName
  FROM productSize
 WHERE productNo = #{productNo}

SELECT count(*) FROM (SELECT @ROWNUM:=@ROWNUM+1 as num , A.* FROM (SELECT * FROM product, (SELECT @ROWNUM:=0) AS R WHERE productName LIKE CONCAT('%',#{search},'%')
AND category = #{category}
AND salePrice BETWEEN #{minPrice} and #{maxPrice}
ORDER BY productNo DESC
ORDER BY salePrice ASC
ORDER BY salePrice DESC
ORDER BY productName DESC
) A ) B

SELECT * FROM (SELECT @ROWNUM:=@ROWNUM+1 as num , A.* FROM (SELECT * FROM product, (SELECT @ROWNUM:=0) AS R WHERE productName LIKE CONCAT('%',#{search},'%')
AND category = #{category}
AND salePrice BETWEEN #{minPrice} and #{maxPrice}
ORDER BY productNo DESC
ORDER BY salePrice ASC
ORDER BY salePrice DESC
ORDER BY productName DESC
) A ) B WHERE num BETWEEN #{startPage} and #{endPage}
